import React, { useState } from 'react';
import JSONEditor from './components/JSONEditor';
import FormPreview from './components/FormPreview';

const App: React.FC = () => {
  const [jsonSchema, setJsonSchema] = useState<string>('');
  const [parsedSchema, setParsedSchema] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);

  const handleSchemaChange = (newSchema: string) => {
    setJsonSchema(newSchema);
    try {
      const parsed = JSON.parse(newSchema);
      setParsedSchema(parsed);
      setError(null);
    } catch (err: any) {
      setParsedSchema(null);
      setError('Invalid JSON format');
    }
  };

  return (
    <div className="flex flex-col items-center min-h-screen p-4 bg-gray-50">
      {/* Title with Mixed-Color Gradient Background */}
      <h1
        className="text-3xl lg:text-4xl font-bold text-white bg-gradient-to-r from-purple-500 via-pink-500 to-red-500 px-6 py-3 rounded-lg cursor-pointer hover:opacity-90 transition duration-300 text-center mb-6 mt-12"
        onClick={() => alert('Dynamic Form Generator clicked!')}
      >
        Dynamic Form Generator
      </h1>

      {/* Responsive Layout */}
      <div className="flex flex-col lg:flex-row w-full lg:w-3/4 gap-6">
        {/* JSON Editor */}
        <JSONEditor
          value={jsonSchema}
          onChange={handleSchemaChange}
          error={error}
        />

        {/* Form Preview */}
        <FormPreview schema={parsedSchema} />
      </div>
    </div>
  );
};

export default App;
