import React from 'react';

interface JSONEditorProps {
  value: string;
  onChange: (value: string) => void;
  error: string | null;
}

const JSONEditor: React.FC<JSONEditorProps> = ({ value, onChange, error }) => {
  const handleInputChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    onChange(e.target.value);
  };

  // Auto-resize logic
  const handleResize = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const textarea = e.target;
    textarea.style.height = 'auto'; // Reset height
    textarea.style.height = `${textarea.scrollHeight}px`; // Set new height based on content
  };

  return (
    <div className="w-full lg:w-1/2 p-6 bg-white rounded-xl shadow-lg transition-all duration-500 hover:shadow-2xl">
      <textarea
        className="w-full h-auto border-2 border-gray-300 rounded-lg p-4 text-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300 resize-none"
        value={value}
        onChange={(e) => { handleInputChange(e); handleResize(e); }}
        placeholder="Enter JSON Schema here..."
        rows={4} // Initial size
      />
      {error && <p className="text-red-500 mt-2">{error}</p>}
    </div>
  );
};

export default JSONEditor;
