import React from 'react';

interface JSONEditorProps {
  value: string;
  onChange: (value: string) => void;
  error: string | null;
}

const JSONEditor: React.FC<JSONEditorProps> = ({ value, onChange, error }) => {
  return (
    <div className="w-full lg:w-1/2 p-6">
      <textarea
        className="w-full h-96 p-4 border-2 border-gray-300 rounded-lg bg-gradient-to-r from-gray-100 via-white to-gray-200 focus:outline-none focus:ring-4 focus:ring-blue-400 focus:border-blue-500 resize-none scrollbar-thin scrollbar-thumb-blue-400 scrollbar-track-gray-100"
        value={value}
        onChange={(e) => onChange(e.target.value)}
        placeholder="Enter JSON Schema here..."
      />
      {error && <p className="text-red-500 mt-2 text-sm">{error}</p>}
    </div>
  );
};

export default JSONEditor;
